    <div>
      <button onClick={() => this.setState({resource: 'posts'})}>Posts</button>
      <button onClick={() => this.setState({resource: 'todos'})}>Todos</button>
    </div>
    {this.state.resource}

    <Router history={history}>
      <div>
        App Navigation Bar
        <Switch>
          {/* Routes */}
        </Switch>
      </div>
    </Router>
  </div>);
};

export default App;
