    title: 'Sex education'
  }, {
    id: 9,
    image: '/images/slide5.jpg',
    imageBg: '/images/slide5b.webp',
    title: 'Elite'
  }, {
    id: 10,
    image: '/images/slide6.jpg',
    imageBg: '/images/slide6b.webp',
    title: 'Black mirror'
  }
];

class App extends Component {
  render() {
    return (<div className="app">
      {/* Main Component */}
      <Slider>
        {movies.slice(0, 7).map(movie => (<Slider.Item movie={movie} key={movie.id}></Slider.Item>))}
      </Slider>
      <Slider>
        {movies.map(movie => (<Slider.Item movie={movie} key={movie.id}></Slider.Item>))}
      </Slider>
    </div>);
  }
}

export default App;
