// HOOK libraries
import {useState, useRef, useEffect} from 'react'

const PADDINGS = 110;

// Hook function receives the width of the current element and the number of
// the Slider component's children
const useSliding = (elementWidth, countElements) => {
  // current slider
  const containerRef = useRef(null);
  // Slider width
  const [containerWidth, setContainerWidth] = useState(0);
  const [distance, setDistance] = useState(0);
  // Number of elements shown in the slider at the same time
  const [totalInViewport, setTotalInViewport] = useState(0)
  const [viewed, setViewed] = useState(0);

  useEffect(() => {
    // get slider width
    const containerWidth = containerRef.current.clientWidth - PADDINGS;
    // set container width
    setContainerWidth(containerWidth);
    // set the number of elements that can be shown in the slider at the same time
    // depending on the copntainer width and the elements width
    setTotalInViewport(3);
  }, [containerRef.current]);

  const handlePrev = () => {
    setViewed(viewed - totalInViewport);
    setDistance(distance + containerWidth);
  }

  const handleNext = () => {
    setViewed(viewed + totalInViewport);
    setDistance(distance - containerWidth)
  }

  const slideProps = {
    style: {
      transform: `translate3d(${distance}px, 0, 0)`
    }
  };

  const hasPrev = distance < 0;
  const hasNext = (viewed + totalInViewport) < countElements;

  return {
    handlePrev,
    handleNext,
    slideProps,
    containerRef,
    hasPrev,
    hasNext
  };
}

export default useSliding;
