import React from 'react';
import cx from 'classnames';
import SliderContext from '../../contexts/SliderContext'
import Mark from './Mark'
import './Item.scss'

// What's inside of a Consumer it's called a Child and it's always a function
const Item = (props) => (<SliderContext.Consumer>
  {
    (value) => {
      const isActive = value.currentSlide && value.currentSlide.id === props.movie.id;

      return (<div ref={value.elementRef} className={cx('item', {'item--open': isActive})}>
        <img src={props.movie.image} alt=""/>
        {isActive && <Mark/>}
      </div>);
    }
  }
</SliderContext.Consumer>);

export default Item;
