import React, {useState, useEffect} from 'react'
import ReactDOM from 'react-dom'
import SeasonDisplay from './SeasonDisplay'
import LoadingSpinner from './LoadingSpinner'

class App extends React.Component {

  state = {
    lat: null,
    errorMessage: ''
  };

  componentDidMount() {
    window.navigator.geolocation.getCurrentPosition((position) => this.setState({lat: position.coords.latitude}), (err) => this.setState({errorMessage: err.message}));
  }

  componentDidUpdate() {
    console.log('My component was updated');
  }

  renderContent() {
    if (this.state.errorMessage && !this.state.lat) {
      return <div>Error: {this.state.errorMessage}</div>;
    }
    if (!this.state.errorMessage && this.state.lat) {
      return <SeasonDisplay lat={this.state.lat}/>;
    }
    return <LoadingSpinner message='Please accept location request'/>;
  }

  render() {
    return (<div className="red border">{this.renderContent()}</div>);
  }
}

ReactDOM.render(<App/>, document.querySelector('#root'));
