  render() {
    return (<div className="ui conatiner">
      <div>
        Select Language:
        <i className="flag uk" onClick={() => this.onLanguageChange('english')}/>
        <i className="flag es" onClick={() => this.onLanguageChange('spanish')}/>
      </div>

      <Router history={history}>
        <div>
          <Switch>
            {/* Routes */}
          </Switch>
        </div>
      </Router>
    </div>);
  }
};

export default App;
