import React from 'react';
import LanguageContext from '../contexts/LanguageContext';

class Field extends React.Component {

  static contextType = LanguageContext;

  render() {

    const text = this.context === 'english'
      ? 'Name'
      : 'Nombre';

    return (<div>
      <label>{text}</label>
      <input/>
    </div>);
  }
};

export default Field;
